import{_ as a,c as r,o as t,ae as f}from"./chunks/framework.CHrjAsYu.js";const d=JSON.parse('{"title":"配置 FFmpeg","description":"","frontmatter":{},"headers":[],"relativePath":"guide/ffmpeg.md","filePath":"guide/ffmpeg.md","lastUpdated":1753866448000}'),p={name:"guide/ffmpeg.md"};function o(l,e,n,m,i,g){return t(),r("div",null,[...e[0]||(e[0]=[f('<h1 id="配置-ffmpeg" tabindex="-1">配置 FFmpeg <a class="header-anchor" href="#配置-ffmpeg" aria-label="Permalink to &quot;配置 FFmpeg&quot;">​</a></h1><h2 id="ffmpeg-是什么" tabindex="-1">FFmpeg 是什么 <a class="header-anchor" href="#ffmpeg-是什么" aria-label="Permalink to &quot;FFmpeg 是什么&quot;">​</a></h2><p><a href="https://ffmpeg.org/" target="_blank" rel="noreferrer">FFmpeg</a> 是知名的开源音视频处理库，被用于许多音视频编解码相关的项目中。</p><h2 id="为什么要使用-ffmpeg" tabindex="-1">为什么要使用 FFmpeg <a class="header-anchor" href="#为什么要使用-ffmpeg" aria-label="Permalink to &quot;为什么要使用 FFmpeg&quot;">​</a></h2><p>本项目使用 FFmpeg 是为了编解码语音和视频，以使 LLOneBot 以 QQ 支持的格式处理语音和视频文件。</p><p>如果你的 Bot 没有处理语音和视频的需求，可以选择不安装 FFmpeg。</p><h2 id="安装-ffmpeg" tabindex="-1">安装 FFmpeg <a class="header-anchor" href="#安装-ffmpeg" aria-label="Permalink to &quot;安装 FFmpeg&quot;">​</a></h2><h3 id="windows-系统" tabindex="-1">Windows 系统 <a class="header-anchor" href="#windows-系统" aria-label="Permalink to &quot;Windows 系统&quot;">​</a></h3><ol><li>在 <a href="https://www.gyan.dev/ffmpeg/builds/ffmpeg-git-essentials.7z" target="_blank" rel="noreferrer">此处</a> 下载官方许可的 FFmpeg；</li><li>将解压后的 <code>ffmpeg.exe</code> 和 <code>ffprobe.exe</code> 放到 llonebot.js 旁；</li></ol><h3 id="linux-系统" tabindex="-1">Linux 系统 <a class="header-anchor" href="#linux-系统" aria-label="Permalink to &quot;Linux 系统&quot;">​</a></h3><p>请使用你所使用的系统的包管理器直接安装 <code>ffmpeg</code> 即可正常使用。</p><h3 id="macos-系统" tabindex="-1">macOS 系统 <a class="header-anchor" href="#macos-系统" aria-label="Permalink to &quot;macOS 系统&quot;">​</a></h3><p>你可以使用 <a href="https://brew.sh/" target="_blank" rel="noreferrer">Homebrew</a> 安装 FFmpeg，也可以 <a href="https://evermeet.cx/ffmpeg/" target="_blank" rel="noreferrer">在此处</a> 手动下载二进制文件安装。</p><p>注意如果你选择手动安装，请确保你下载并安装了 <a href="https://evermeet.cx/ffmpeg/#:~:text=static%20%2D%2Ddisable%2Dffplay-,FFmpeg,-ffmpeg%2D114296%2Dg5ff0eb34d2" target="_blank" rel="noreferrer">FFmpeg</a>、<a href="https://evermeet.cx/ffmpeg/#:~:text=external%20ffmpeg%20libraries-,FFprobe,-ffprobe%2D114296%2Dg5ff0eb34d2" target="_blank" rel="noreferrer">FFprobe</a> 以及 <a href="https://evermeet.cx/ffmpeg/#:~:text=external%20ffprobe%20libraries-,FFplay,-ffplay%2D113169%2Dge1c1dc8347" target="_blank" rel="noreferrer">FFplay</a> 三个库，否则可能会产生意想不到的错误。</p>',14)])])}const h=a(p,[["render",o]]);export{d as __pageData,h as default};
